function ativarMenuMobile(){var n=document.querySelector(".main-nav__btn"),e=document.querySelector(".main-nav__btn--fechar");n.addEventListener("click",function(){document.documentElement.classList.add("main-nav__navmenu--active")}),e.addEventListener("click",function(){document.documentElement.classList.remove("main-nav__navmenu--active")})}$(document).ready(function(){$(".home-slider").slick({arrows:!1,dots:!0,autoplay:!0,autoplaySpeed:2500,speed:2e3}),$(document).on("scroll",function(){$(this).scrollTop()>500?$("a.btn-back").fadeIn():$("a.btn-back").fadeOut()}),$("a.btn-back").click(function(){return $("html, body").animate({scrollTop:0},1e3),!1})}),ativarMenuMobile();