function ativarMenuMobile(){var n=document.querySelector(".main-nav__btn"),e=document.querySelector(".main-nav__btn--fechar");n.addEventListener("click",function(){document.documentElement.classList.add("main-nav__navmenu--active")}),e.addEventListener("click",function(){document.documentElement.classList.remove("main-nav__navmenu--active")})}ativarMenuMobile();